var siteNav=document.querySelector(".main-nav"),toggleBtn=document.querySelector(".main-nav__toggle");siteNav.classList.remove("main-nav--nojs"),toggleBtn.addEventListener("click",function(){siteNav.classList.contains("main-nav--closed")?(siteNav.classList.remove("main-nav--closed"),siteNav.classList.add("main-nav--opened")):(siteNav.classList.add("main-nav--closed"),siteNav.classList.remove("main-nav--opened"))});var showModalBtn=document.querySelectorAll(".js-show-modal"),overlay=document.querySelector(".overlay"),modal=document.querySelector(".modal");function toggleModal(a){a.preventDefault(),modal.classList.contains("modal--add-cart")?modal.classList.remove("modal--add-cart"):modal.classList.add("modal--add-cart"),overlay.classList.contains("overlay--show")?overlay.classList.remove("overlay--show"):overlay.classList.add("overlay--show")}for(var i=0;i<showModalBtn.length;i++)showModalBtn[i].addEventListener("click",toggleModal);overlay.addEventListener("click",toggleModal);